import { ADD_TRANSACTION, REMOVE_TRANSACTION } from "./transactionTypes";

const initialState = {
  transactionHistory: [],
};

const transactionReducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_TRANSACTION: {
      const udpatedTransactionHistory = state.transactionHistory;
      udpatedTransactionHistory.push(action.payload);

      // return { ...state, transactionHistory: udpatedTransactionHistory };
      return {
        transactionHistory: [
          {
            id: 1596199359844,
            name: "txt",
            amount: "1",
          },
        ],
      };
    }

    case REMOVE_TRANSACTION: {
      // console.log(action);
      const updatedTransactionList = state.transactionHistory.filter(
        (transactionDetails) => transactionDetails.id !== action.payload
      );
      state.transactionHistory.push(action.payload);
      return {
        ...state,
        transactionHistory: updatedTransactionList,
      };
    }
    default:
      return state;
  }
};
// console.log(transactionHistory);

export default transactionReducer;
