import React from "react";
import { useState } from "react";
import { useDispatch } from "react-redux";
import { addTransaction } from "../redux/transactions/transactionAction";

function AddTransaction() {
  const [transactionName, setTransactionName] = useState(undefined);
  const [transactionValue, setTransactionValue] = useState(undefined);
  const dispatch = useDispatch();
  const transactionData = () => {
    return {
      name: transactionName,
      amount: transactionValue,
    };
  };
  return (
    <div>
      <form id="form">
        <div class="form-control">
          <label>Text</label>
          <input
            type="text"
            placeholder="Enter text..."
            value={transactionName}
            onChange={(e) => setTransactionName(e.target.value)}
          />
        </div>
        <div class="form-control">
          <label>
            Amount <br />
            (negative - expense, positive - income)
            <br />
          </label>
          <input
            type="number"
            placeholder="Enter amount..."
            value={transactionValue}
            onChange={(e) => setTransactionValue(e.target.value)}
          />
        </div>
        <button
          class="btn"
          onClick={() => dispatch(addTransaction(transactionData))}
        >
          Add transaction
        </button>
      </form>
    </div>
  );
}

export default AddTransaction;
